mapper.select:
select 

from netbar_membertype
where memberTypePlanID = #{membertypeplanid,jdbcType=INTEGER}
  and memberTypeID = #{membertypeid,jdbcType=INTEGER}

mapper.delete:
delete from netbar_membertype
where memberTypePlanID = #{membertypeplanid,jdbcType=INTEGER}
  and memberTypeID = #{membertypeid,jdbcType=INTEGER}

mapper.insert:
insert into netbar_membertype (memberTypePlanID, memberTypeID, parasTypeName, 
  dataVersion, gid, theOrder
  )
values (#{membertypeplanid,jdbcType=INTEGER}, #{membertypeid,jdbcType=INTEGER}, #{parastypename,jdbcType=VARCHAR}, 
  #{dataversion,jdbcType=INTEGER}, #{gid,jdbcType=INTEGER}, #{theorder,jdbcType=INTEGER}
  )

mapper.update:
update netbar_membertype
set parasTypeName = #{parastypename,jdbcType=VARCHAR},
  dataVersion = #{dataversion,jdbcType=INTEGER},
  gid = #{gid,jdbcType=INTEGER},
  theOrder = #{theorder,jdbcType=INTEGER}
where memberTypePlanID = #{membertypeplanid,jdbcType=INTEGER}
  and memberTypeID = #{membertypeid,jdbcType=INTEGER}
